mapper.insert:
INSERT INTO rooms (title, status, capacity, created_at, updated_at) VALUES
(#{room.title}, #{room.status}, #{room.capacity}, #{createdAt},
#{updatedAt});

mapper.select:
SELECT r.id, r.title, r.capacity, m.nickname AS 'manager', r.status FROM rooms r JOIN room_players rp ON r.id = rp.room_id JOIN members m ON rp.member_id = m.id WHERE rp.role = 'MANAGER'
AND r.id < #{cursor}
ORDER BY 1 DESC LIMIT #{size};

SELECT IF(COUNT(*)=1, true, false)
FROM rooms
WHERE id=#{id}

mapper.update:
UPDATE rooms
SET title=#{room.title}, status=#{room.status},
capacity=#{room.capacity}, updated_at=#{updatedAt}
WHERE id=#{room.id}
